tasks {
    withType<JavaCompile> {
        options.encoding = "UTF-8"
    }

    jar {
        manifest {
            attributes(
                mapOf(
		            "Implementation-Title" to project.name,
                    "Implementation-Version" to project.version
		        )
            )
        }
    }
}
